package com.shristi.assignments;

public class EvenNumbersUpto20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int range = 20;
        for (int number=1; number<=range;number++)  {
        	if (number % 2 == 0)  {
        		System.out.println(number);
        	}
        }
	}

}
