package com.training.demos;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 30, y= 20;
		switch (x+y)  {
		case 30:
			System.out.println("Sum " +(x+y));
			break;
		case 50:
			System.out.println("Sum " +(x+y));
			break;
		default:
			System.out.println("No match");
		}		

	}

}
