package com.training.demos;

public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 0;
		while (x<10)  {
			System.out.println(x);
			x++;
		}
        int y = 10;
        do {
        	System.out.println(y);
        	y++;
        }
        while (y<20);
	}

}
